package com.project.rms.Shop

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.project.rms.R


class ssh_ShopActivity : AppCompatActivity() {

    var ShopList = arrayListOf<ssh_Shop_item>(
        ssh_Shop_item("data:image/jpeg;base64,","목록1"),
        ssh_Shop_item("data:image/jpeg;base64,","목록2"),
        ssh_Shop_item("data:image/jpeg;base64,","목록3"),
        ssh_Shop_item("data:image/jpeg;base64,","목록4"),
        ssh_Shop_item("data:image/jpeg;base64,","목록5")
    )

    // itemList.add(ssy_Recipe_Litem(RCP_SEQ, RCP_NM, RCP_PARTS_DTLS,ATT_FILE_NO_MAIN,ready_parts)) api에 있는 제품을 리스트에 추가할때 사용

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.ssh_activity_shop)

        val recyclerView = findViewById<RecyclerView>(R.id.shoplist)
        recyclerView.layoutManager = LinearLayoutManager(this)
        var ShopAdapter = ssh_ShopAdapter(ShopList)
        recyclerView.adapter = ShopAdapter
    }
}
